const allBourbons = () => {
    return dispatch => {
        dispatch({type: 'LOADING_BOURBONS'})
        fetch("http://localhost:3001/bourbons")
        .then(response => response.json())
        .then(bourbonData => dispatch({type: 'BOURBONS', payload: bourbonData}))
    }
}

const newBourbon = bourbon => {
    return dispatch => {
      dispatch({type: 'NEW_BOURBON'})
      fetch()
      bourbon
    };
  };

const deleteBourbon = id => {
    return {
      type: 'DELETE_Bourbon',
      id
    };
  };

export {
    allBourbons,
    newBourbon,
    deleteBourbon
  }
